sys_variable_value.value:
(function execute(inputs, outputs) {
	// Troubleshooting 
  	if (gs.getProperty('x_snc_dp1_demo.dp1debug') == "true") {
     	gs.info('DP1Debug - (9) Cluster JSON Builder Entrypoint'); 
    }

  // Create Dell PowerOne "logical/clusters" POST body from saved proposal and user-input name
	var responseObj = JSON.parse(inputs.dp1_response);
	var totalCores = responseObj.serverInfo.cpuSocketCount *
					 responseObj.serverInfo.cpuCoreCount *
					 responseObj.proposedServers.length; 
	var totalRAM = responseObj.serverInfo.totalMemory *
				   responseObj.proposedServers.length;

	var realObj = {
		"summary" : {
			"name": inputs.cluster_name,
			"requirements": {
				"platform": {
					"platformType": "vSphere+NSX",
					"version": "vSphere 6.7",
					"versionID": 2
				},
				"server": {
					"totalCpuCores": totalCores,
					"totalRAM": totalRAM
				},
				"storage": {
					// This number is not part of the response object?
					"totalClusterStorageTB": parseInt(inputs.storage_size,10),
					"dataReduction": true,
					"encryption": true
				}
			},
			"assets": {
				"server": [],
				"storage": {
					"arrayInfo": {
						"id": responseObj.proposedStorage.id,
						"name": responseObj.proposedStorage.name,
						"type": responseObj.proposedStorage.type,
						"model": responseObj.proposedStorage.model
					},
					"volumeCapacity": responseObj.proposedStorage.volumeInfo.volumeCapacity,
					"volumeCount": responseObj.proposedStorage.volumeInfo.volumeCount
				}
			}
		}
	}

	// Loop through server assets to add them to the blank array ...
	for (var svr = 0; svr < responseObj.proposedServers.length; svr++) {
		realObj.summary.assets.server[svr] = responseObj.proposedServers[svr].serviceTag;
	}

  	var tempString = JSON.stringify(realObj);
    outputs.post_body = tempString;

    // Troubleshooting
	if (gs.getProperty('x_snc_dp1_demo.dp1debug') == "true") {
  		gs.info('DP1Debug - (10) Cluster Request Body : ' + tempString);
    }  	

})(inputs, outputs);

